INSERT INTO kcv.people (personId, name, description, shortDescription) VALUES (227, 'Giganten', 'Metamorphosen: Niet slechts de aarde, zelfs de hoge hemel werd onveilig: Giganten vielen de goden aan -- zo wordt verteld -- en stapelden gebergten op elkaar tot sterrenhoogte. Maar toen schoot Jupiter zijn snelle bliksempijl dwars door Olympus’ berg en stootte Pelion van Ossa’s bergrug. De woeste reuzen werden door hun eigen vracht geplet en Moeder Aarde -- zegt men -- werd bevochtigd door het bloed van haar eigen zoons; warm vocht, waaraan zij weer nieuw leven schonk, want om nog enig teken van haar telgen te bewaren deed zij er nieuwe mensen uit ontstaan. Maar ook dat kroost gedroeg zich godsvijandig, was belust op moord en doodslag en vechtziek, onmiskenbaar een uit bloed gesproten ras.', null);
